<div class="modal fade" id="termsModal" tabindex="-1" role="dialog" aria-labelledby="@lang('terms.title')" aria-hidden="true">
    <div class="modal-dialog">
        <div class="modal-content">
            <div class="modal-header">
                <h3 class="modal-title">@lang('terms.title')</h3>
            </div>

            <div class="modal-body">@lang('terms.terms')</div>

            <div class="modal-footer">
                <button type="button" class="btn btn-primary" data-dismiss="modal">@lang('terms.ok')</button>
            </div>
        </div>
    </div>
</div>